              <FormInput placeholder={"SAPS 2"} onChangeText={setSaps2} type="number" type="decimal-pad" maxLength={Number("8")} />
              <FormInput placeholder={"APACHE 2"} onChangeText={setApache2} type="decimal-pad" type="number" maxLength={Number("8")} />
              <FormInput placeholder={"SOFA"} onChangeText={setSofa} type="decimal-pad" type="number"  maxLength={Number("8")} />
